import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";
import SnackOrBoozeApi from "./Api";
export const NewItemForm = () => {

    const [form, setNewForm] = useState({});
    const initialState = {
        name: "",
        description: "",
        recipe: "",
        serve: ""
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(form);
        setNewForm(initialState);
    };

    useEffect(() => {
        async function addItem() {
            console.log("form::::::;", form)
            form.type === "drink" ? await SnackOrBoozeApi.addDrink(form) : await SnackOrBoozeApi.addSnack(form);
        }
        addItem();
    }, [handleSubmit()]);


    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewForm({
            ...form,
            id: form.name, [name]: value
        });
    };



    return (
        <>
             <form onSubmit={handleSubmit}>
                <label htmlFor="name">name</label>  
                <input value={form.name}
                    name="name"
                    placeholder="name"
                onChange={handleChange}/>

                <label htmlFor="desc">description</label>  
                <textarea name="desc"
                    value={form.description}
                    placeholder="description"
                    onChange={handleChange}/>

                <label htmlFor="recipe">recipe</label>  
                <textarea value={form.recipe}
                    name="recipe"
                    placeholder="recipe"
                    onChange={handleChange}/>

                <label htmlFor="serve">serve</label>  
                <input value={form.serve}
                    name="serve"
                    placeholder="recipe"
                    onChange={handleChange} />

                <label htmlFor="type">type of item</label>  
                <select value={form.serve} name="type"
                    onChange={handleChange}>
                    <option value="">choose type of item </option>
                    <option value="drink">Drink </option>
                    <option value="snack">Snack</option>

                </select>

                <button>submit</button>
             </form>
        </>
    )
}
